#!/usr/bin/env python# -*-coding:utf-8-*-import stringimport randomfrom PIL import Image, ImageDraw, ImageFilter, ImageFontHEIGHT = 200WIDTH = 400''' 生成随机字母 '''def gen_letters(num=4):    return ''.join(random.sample(string.ascii_letters, num))''' 生成随机颜色 '''def rand_col():    return (random.randint(50, 200), random.randint(50, 100), random.randint(50, 100))def makeImag():    im = Image.new('RGB', size=(WIDTH, HEIGHT), color=(255, 255, 255)) # 生成图片    draw = ImageDraw.Draw(im) # 生成画笔    ''' 画线条 '''    for i in range(20):        draw.line(            [                (random.randint(0, WIDTH // 4), random.randint(0, HEIGHT)),                (random.randint(WIDTH - 30, WIDTH - 10), random.randint(0, HEIGHT))            ],            fill=rand_col()        )            font = ImageFont.truetype('arial.ttf', 80)    text = gen_letters()    font_width, font_height = font.getsize(text) # 获取四个字母的size    x = (WIDTH - font_width) / 2    y = (HEIGHT - font_height) / 2    ''' 单个画字符 '''    for letter in text:        draw.text([x, y], letter, fill=rand_col(), font=font)        x += font_width / len(text)    im = im.filter(ImageFilter.GaussianBlur) # 模糊图片    im.save('demo.png', 'PNG') # 输出图片if __name__ == '__main__':    print(gen_letters(num=4))    makeImag()